properties.property:
local feeds = { "https://feeds.bbci.co.uk/news/science_and_environment/rss.xml", "https://beta.nsf.gov/rss/rss_www_news.xml", "https://www.newscientist.com/section/news/feed/" }
function publish() for i,feed in ipairs(feeds) do events = getFeed(feed)
if esp_inject(events) == false then return true end end
return false end
function getFeed(feed) local request = {}
request.url = feed request.tolua = true
local data = esp_sendHttp(request) local events = {} local index = 1
for i,entry in ipairs(data.response.rss.item_array) do local event = {} event.id = entry.item.guid["*value"] event.source = feed event.title = entry.item.title event.date = entry.item.pubDate event.description = entry.item.description event.link = entry.item.link event._opcode = "upsert" events[index] = event index = index + 1 end
return events end
